import React, { useState } from 'react';
import axios from 'axios';

const VendorForm = ({vendorId, setVendorId}) => {
  const [name, setName] = useState('');

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Send a POST request to the Express.js backend
    axios.post('http://localhost:5001/api/vendor', { name })
      .then((response) => {
        // Retrieve the vendor ID from the response
        const { vendorId } = response.data;
        setVendorId(vendorId);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Vendor Name:
          <input type="text" value={name} onChange={handleInputChange} />
        </label>
        <button type="submit">Submit</button>
      </form>

      {vendorId && (
        <div>Vendor ID: {vendorId}</div>
      )}
    </div>
  );
};

export default VendorForm;
